import { Component, OnInit } from "@angular/core";
import { products } from "src/assets/products-data.json";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent implements OnInit {
  products = products;
  selectedProperties: string[] = [];
  selectedManufacturers: string[] = [];

  constructor() {}
  ngOnInit(): void {
    throw new Error("Method not implemented.");
  }

  filterByProperties() {
    if (this.selectedProperties.length > 0) {
      this.products = this.products.filter(
        (product: { properties: string | string[] }) => {
          return this.selectedProperties.every((prop) =>
            product.properties.includes(prop)
          );
        }
      );
    } else {
      this.products = products;
    }
  }

  filterByManufacturers() {
    if (this.selectedManufacturers.length > 0) {
      this.products = this.products.filter(
        (product: { manufacturer: string }) => {
          return this.selectedManufacturers.includes(product.manufacturer);
        }
      );
    } else {
      this.products = products;
    }
  }
}
<h1>Product</h1>
<div>
  [options]="propertiesOptions" (selectionChanged)="applyFilters()">
  [options]="manufacturerOptions" (selectionChanged)="applyFilters()">

  <div class="product-grid">
    <div class="product-grid-item" *ngFor="let product of filteredProducts">
      <div class="product-grid-item__name">{{ product.name }}</div>
      <div class="product-grid-item__manufacturer">
        {{ product.manufacturer }}
      </div>
      <div class="product-grid-item__price">{{ product.price }}</div>
      <div class="product-grid-item__properties">
        <ul>
          <li *ngFor="let property of product.properties">{{ property }}</li>
        </ul>
      </div>
    </div>
  </div>
</div>
