import { Component, OnInit } from "@angular/core";
import productsData from "src/assets/data/products.json";

interface Product {
  name: string;
  price: number;
  properties: string[];
  manufacturer: string;
}

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent {
  products = productsData;

  filteredProducts: Product[];

  selectedProperties: string[] = [];
  selectedManufacturer: string | undefined;

  constructor() {
    this.filteredProducts = this.products;
  }

  applyFilters() {
    let filteredProducts = this.products;

    if (this.selectedProperties.length > 0) {
      filteredProducts = filteredProducts.filter((product) => {
        for (let property of this.selectedProperties) {
          if (!product.properties.includes(property)) {
            return false;
          }
        }
        return true;
      });
    }

    if (this.selectedManufacturer) {
      filteredProducts = filteredProducts.filter((product) => {
        return product.manufacturer === this.selectedManufacturer;
      });
    }

    this.filteredProducts = filteredProducts;
  }
}
