import { Component, OnInit } from "@angular/core";
import products from ".data/products-data.json";

interface Product {
  name: string;
  price: number;
  properties: string[];
  manufacturer: string;
  visible?: boolean;
}

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent implements OnInit {
  products: Product[] = products;
  properties: string[] = [];
  manufacturers: string[] = [];

  ngOnInit(): void {
    this.initializeFilters();
  }

  private initializeFilters() {
    this.products.forEach((product) => {
      product.properties.forEach((property) => {
        if (!this.properties.includes(property)) {
          this.properties.push(property);
        }
      });
      if (!this.manufacturers.includes(product.manufacturer)) {
        this.manufacturers.push(product.manufacturer);
      }
    });
  }

  onPropertiesFilterChange() {
    const selectedProperties = Array.from(
      document.querySelectorAll("#properties option:checked")
    ).map((option) => option.value);

    this.products.forEach((product) => {
      const productMatchesFilter = selectedProperties.every((property) =>
        product.properties.includes(property)
      );
      product.visible = productMatchesFilter;
    });
  }

  onManufacturerFilterChange() {
    const selectedManufacturers = Array.from(
      document.querySelectorAll("#manufacturer option:checked")
    ).map((option) => option.value);

    this.products.forEach((product) => {
      const productMatchesFilter = selectedManufacturers.includes(
        product.manufacturer
      );
      product.visible = productMatchesFilter;
    });
  }
}
