import { Component, OnInit } from "@angular/core";
import productsData from "src/assets/data/products.json";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent {
  products: any[] = productsData;
  selectedProperties: any;
  selectedManufacturer: string | undefined;

  filterProducts() {
    this.products = productsData.filter((product) => {
      let matchesProperties = true;
      let matchesManufacturer = true;
      if (this.selectedProperties) {
        matchesProperties = product.properties.includes(
          this.selectedProperties
        );
      }
      if (this.selectedManufacturer) {
        matchesManufacturer =
          product.manufacturer === this.selectedManufacturer;
      }
      return matchesProperties && matchesManufacturer;
    });
  }
}
<div class="grid">
  <div class="grid-item" *ngFor="let product of products">
    <h2>{{ product.name }}</h2>
    <p>Price: {{ product.price }}</p>
    <p>Manufacturer: {{ product.manufacturer }}</p>
    <p>Properties: {{ product.properties.join(", ") }}</p>
  </div>
</div>

<label for="properties">Properties:</label>
<select
  id="properties"
  [(ngModel)]="selectedProperties"
  (change)="filterProducts()"
>
  <option value="">All</option>
  <option *ngFor="let prop of propertiesList" [value]="prop">{{ prop }}</option>
</select>

<label for="manufacturer">Manufacturer:</label>
<select
  id="manufacturer"
  [(ngModel)]="selectedManufacturer"
  (change)="filterProducts()"
>
  <option value="">All</option>
  <option *ngFor="let manu of manufacturerList" [value]="manu">
    {{ manu }}
  </option>
</select>
