import { Component, OnInit } from "@angular/core";
import productsData from "src/assets/data/products.json";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent {
  products = productsData;
  properties = ["Vegan", "Gluten free", "Organic"];
  manufacturers = ["Company A", "Company B", "Company C"];
  selectedProperties = [];
  selectedManufacturer = "";
}
<div class="container products">
  <div class="left-container">Properties</div>
  <div class="right-container">Manufctory</div>
</div>

