import { Component, OnInit } from "@angular/core";
import { HttpClient } from "@angular/common/http";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent implements OnInit {
  products: any[] | undefined;
  filteredProducts: any[] | undefined;
  propertiesOptions: string[] | undefined;
  manufacturerOptions: string[] | undefined;
  selectedProperties: string[] = [];
  selectedManufacturers: string[] = [];

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.http.get<any[]>("src/data.json").subscribe((data) => {
      this.products = data;
      this.filteredProducts = data;
      this.propertiesOptions = this.getUniqueProperties(data);
      this.manufacturerOptions = this.getUniqueManufacturers(data);
    });
  }

  getUniqueProperties(data: any[]): string[] {
    let properties: string[] = [];
    data.forEach((product) => {
      product.properties.forEach((property) => {
        if (!properties.includes(property)) {
          properties.push(property);
        }
      });
    });
    return properties;
  }

  getUniqueManufacturers(data: any[]): string[] {
    let manufacturers: string[] = [];
    data.forEach((product) => {
      if (!manufacturers.includes(product.manufacturer)) {
        manufacturers.push(product.manufacturer);
      }
    });
    return manufacturers;
  }

  applyFilters(): void {
    let filteredByProperties = this.filterByProperties(this.products);
    let filteredByManufacturers = this.filterByManufacturers(this.products);
    this.filteredProducts = filteredByProperties.filter((product) =>
      filteredByManufacturers.includes(product)
    );
  }

  filterByProperties(products: any[]): any[] {
    if (this.selectedProperties.length === 0) {
      return products;
    }
    return products.filter((product) => {
      let matches = true;
      this.selectedProperties.forEach((property) => {
        if (!product.properties.includes(property)) {
          matches = false;
        }
      });
      return matches;
    });
  }

  filterByManufacturers(products: any[]): any[] {
    if (this.selectedManufacturers.length === 0) {
      return products;
    }
    return products.filter((product) => {
      return this.selectedManufacturers.includes(product.manufacturer);
    });
  }
}
