import { Component, OnInit } from "@angular/core";
import productsData from "src/assets/data/products.json";
import { HttpClient } from "@angular/common/http";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent {
  products: any[];
  filteredProducts: any[];
  properties: string[];
  selectedProperties: string[] = [];
  manufacturers: string[];
  selectedManufacturers: string[] = [];

  constructor() {
    this.products = productsData;
    this.filteredProducts = this.products;
    this.properties = this.getUniqueProperties();
    this.manufacturers = this.getUniqueManufacturers();
  }

  filterProducts() {
    let filteredProducts = this.products;

    // Filter by properties
    if (this.selectedProperties.length > 0) {
      filteredProducts = filteredProducts.filter((product) => {
        return this.selectedProperties.every((property) => {
          return product.properties.includes(property);
        });
      });
    }

    // Filter by manufacturers
    if (this.selectedManufacturers.length > 0) {
      filteredProducts = filteredProducts.filter((product) => {
        return this.selectedManufacturers.includes(product.manufacturer);
      });
    }

    this.filteredProducts = filteredProducts;
  }

  getUniqueProperties() {
    let properties = [];

    this.products.forEach((product) => {
      product.properties.forEach((property) => {
        if (!properties.includes(property)) {
          properties.push(property);
        }
      });
    });

    return properties;
  }

  getUniqueManufacturers() {
    let manufacturers = [];

    this.products.forEach((product) => {
      if (!manufacturers.includes(product.manufacturer)) {
        manufacturers.push(product.manufacturer);
      }
    });

    return manufacturers;
  }
}
