import { Component, OnInit } from "@angular/core";
import productsData from "src/assets/data/products.json";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent {
  products = productsData;
  properties = ["Vegan", "Gluten free", "Organic"];
  manufacturers = ["Company A", "Company B", "Company C"];
  selectedProperties = [];
  selectedManufacturer = "";
}
<div>
  <div>
    <h2>Filters</h2>
    <label>Properties:</label>
    <div *ngFor="let property of properties">
      <input type="checkbox" [value]="property" (change)="filterProducts()" />
      {{ property }}
    </div>
    <label>Manufacturer:</label>
    <select [(ngModel)]="selectedManufacturer" (change)="filterProducts()">
      <option value="">All</option>
      <option *ngFor="let manufacturer of manufacturers" [value]="manufacturer">
        {{ manufacturer }}
      </option>
    </select>
  </div>
  <div>
    <h2>Products</h2>
    <div class="product-grid">
      <div *ngFor="let product of products" [hidden]="!product.visible">
        <img [src]="product.image" />
        <p>{{ product.name }}</p>
        <p>{{ product.price }}</p>
      </div>
    </div>
  </div>
</div>
