import { Component } from "@angular/core";
import productsData from "src/assets/data/products.json";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.scss"],
})
export class AppComponent {
  products = productsData;
  propertyOptions: { label: string; value: string; selected: boolean }[];
  manufacturerOptions: { label: string; value: string; selected: boolean }[];
  filteredProducts: any[];

  constructor() {
    this.propertyOptions = this.getPropertyOptions();
    this.manufacturerOptions = this.getManufacturerOptions();
    this.filteredProducts = this.products;
  }

  getPropertyOptions() {
    const propertyOptions: {
      label: string;
      value: string;
      selected: boolean;
    }[] = [];
    this.products.forEach((product) => {
      product.properties.forEach((property) => {
        if (!propertyOptions.some((option) => option.value === property)) {
          propertyOptions.push({
            label: property,
            value: property,
            selected: false,
          });
        }
      });
    });
    return propertyOptions;
  }

  getManufacturerOptions() {
    const manufacturerOptions: {
      label: string;
      value: string;
      selected: boolean;
    }[] = [];
    this.products.forEach((product) => {
      const manufacturer = product.manufacturer;
      if (
        !manufacturerOptions.some((option) => option.value === manufacturer)
      ) {
        manufacturerOptions.push({
          label: manufacturer,
          value: manufacturer,
          selected: false,
        });
      }
    });
    return manufacturerOptions;
  }

  filterProducts() {
    const selectedProperties = this.propertyOptions.filter(
      (option) => option.selected
    );
    const selectedManufacturers = this.manufacturerOptions.filter(
      (option) => option.selected
    );
    this.filteredProducts = this.products.filter((product) => {
      const hasSelectedProperties = selectedProperties.every((property) =>
        product.properties.includes(property.value)
      );
      const hasSelectedManufacturers = selectedManufacturers.some(
        (manufacturer) => product.manufacturer === manufacturer.value
      );
      return hasSelectedProperties && hasSelectedManufacturers;
    });
  }
}
<div class="container">
  <div class="left">
    <div class="filters">
      <div class="filter properties">
        <h4 class="filter-titel">Filter</h4>
        <p class="properties-title">Eigenschaften</p>
        <ng-container *ngFor="let option of propertyOptions">
          <div class="checkbox-list">
            <span>{{ option.label }}</span>
            <input
              type="checkbox"
              [value]="option.value"
              [(ngModel)]="option.selected"
              (change)="filterProducts()"
            />
          </div>
        </ng-container>
      </div>
      <div class="filter">
        <p class="manufactory-titel">Hersteller</p>
        <ng-container *ngFor="let option of manufacturerOptions">
          <div class="checkbox-list">
            <span>{{ option.label }}</span>
            <input
              type="checkbox"
              [value]="option.value"
              [(ngModel)]="option.selected"
              (change)="filterProducts()"
            />
          </div>
        </ng-container>
      </div>
    </div>
  </div>
  <div class="right">
    <div class="product-grid">
      <div
        class="card"
        style="width: 18rem"
        *ngFor="let product of filteredProducts"
      >
        <img class="card-img-top" [src]="product.image" />
        <div class="card-body">
          <p>{{ product.name }}</p>
          <p class="product-base-price">{{ product.price }} €</p>
          <p class="product-base-price">
            {{ product.basePrice.price }} {{ product.basePrice.unit }}
          </p>
          <p>Preis inkl. MwSt., zzgl.Versand</p>
        </div>
      </div>
    </div>
  </div>
</div>
